    -moz-hyphens: auto;
    -ms-hyphens: auto;
    -webkit-hyphens: auto;
   // hyphens: auto;
  }

  .popup-btn{
    width: 60%;
    display: block;
    margin: 0 auto;
    margin-top: 3rem;
  }

  //Open states
  &:target {
    opacity: 1;
    visibility: visible;
  }

  &:target &__content {
    opacity: 1;
    transform: translate(-50%, -50%) scale(1);
  }

