.btn{

  &:link,
  &:visited{
    margin-top: 2rem;
    font-size: $default-font-size;
    font-weight: 400;
    text-transform: uppercase;
    text-decoration: none;
    padding: 1.5rem 4rem;
    display : inline-block;
    border-radius: 10rem;
    transition: all 0.2s;
    position: relative;

  }

  &:hover{
    transform: translateY(-.3rem);
    box-shadow: 0px 1rem 2rem rgba($color-black, 0.6);

    &::after{
      transform: scaleX(1.5) scaleY(1.4);
      opacity: 0;
    }

  }

  &:active{
    transform: translateY(-.1rem);
    box-shadow: 0px .5rem 1rem rgba($color-black, 0.6);

  }

  &-white{
    background-color: $color-button-white;
    color: $color-grey-dark;


    &::after{
      background-color: white;
    }

  }

  &-gray{
    background-color: $color-button-white;
    color: $color-black;
  border: solid black 1px;

  //  &::after{
   //   background-color: $color-primary;
  //    color: $color-button-white;
  //  }

    &:hover{
      background-color: $color-primary;
      color: $color-button-white;
      border: solid $color-primary 1px;
    }
  }

  &-popup{
    width: max-content !important;
    display: block !important;
    margin: 0 auto !important;
    margin-top: 3rem !important;

    &:hover{
      border: solid $color-primary 1px;
    }

    &::after{
      content: none !important;
    }
  }

  &::after{
    content: "" ;
    display: inline-block;
    height: 100%;
    width: 100%;
    border-radius: 10rem;
    position: absolute;
    top: 0;
    left: 0;
    z-index: -1;
    transition: all .4s;
  }

  &-animated{
    animation: moveInButton .5s ease-out .75s;
    animation-fill-mode: backwards;
  }
}

.btn-text{
  &:link,
  &:visited{
    color: $color-primary;
    display:inline-block;
    text-decoration: none;
    border-bottom: 1px solid $color-primary;
    padding: 3px;
    font-size: $default-font-size; // 1.3 rem feature box
    transition: all .2s;
  }

  &:hover{
    background-color: $color-primary;
    color: $color-button-white;
    box-shadow: 0 1rem 2rem rgba($color-black, .15) ;
    transform: translateY(-2px);
  }

  &active{
    box-shadow: 0 .5rem 1rem rgba($color-black, .15) ;
    transform: translateY(0);

  }
}

