sources.sass\\components\\_popup.scss:
.popup {
  height: 100vh;
  width: 100%;
  position: fixed;
  background-color: rgba($color-black, 0.8);
  top: 0;
  left: 0;
  z-index: 3000;
  opacity: 0;
  visibility: hidden;
  transition: all 0.3s;

  &__content {
    @include absCenter;

    width: 75%;
    background: $color-white;
    box-shadow: 0 2rem 4rem rgba($color-black, 0.2);
    border-radius: 3px;
    display: table;
    overflow: hidden;
    opacity: 0;
    transform: translate(-50%, -50%) scale(0.25);
    transition: all 0.5s 0.2s;

    @include respond(tab-land) {
      width: 90%;
      display: block;
      position: relative;
    }
  }

  &__left {
    width: 33.3333333%;
    display: table-cell;

    @include respond(tab-land) {
     display: none;

    }
  }

  &__list-text{
    li{
      font-size: $default-font-size;
    }
  }

  &__right {
    width: 66.6666667%;
    display: table-cell;
    vertical-align: middle;
    padding: 3rem 5rem;

    @include respond(tab-land) {
      width: 100%;
      display: inline-block;
      position: relative;

      text-align: center;
    }
  }

  &__img {
    display: inline-block;

    @include respond(tab-land) {
     display: none;

    }
  }

  &__text {
    font-size: 1.4rem;
    margin-bottom: 4rem;

    -moz-column-count: 1;
    column-count: 1;

    -moz-hyphens: auto;
    -ms-hyphens: auto;
    -webkit-hyphens: auto;
    hyphens: auto;
    text-align: left;

    @include respond(tab-port) {
      font-size: 1.6rem;
    }
  }

  &:target {
    opacity: 1;
    visibility: visible;
  }

  &:target &__content {
    opacity: 1;
    transform: translate(-50%, -50%) scale(1);
  }

  &__close {
    &:link,
    &:visited {
      color: $color-grey-dark;
      position: absolute;
      top: 2.5rem;
      right: 2.5rem;
      font-size: 3rem;
      text-decoration: none;
      display: inline-block;
      transition: all 0.2s;
      line-height: 1;
    }

    &:hover {
      color: $color-primary;
    }
  }

  @include respond(phone) {
    &__left {
      display: none;

    }
    &__img {
      display: none;
    }
  }
}

